import { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import Header from '@/components/Header';
import { Search, Filter, Download } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useAuthQuery, useAuthApiRequest } from '@/hooks/use-auth-query';
import { useAuth } from '@/contexts/AuthContext';

interface OrderFilters {
  subOrderNo: string;
  status: string;
  paymentStatus: string;
  dateFrom: string;
  dateTo: string;
}

export default function Orders() {
  const [filters, setFilters] = useState<OrderFilters>({
    subOrderNo: '',
    status: '',
    paymentStatus: '',
    dateFrom: '',
    dateTo: '',
  });
  const [handleDuplicates, setHandleDuplicates] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: orders, isLoading } = useAuthQuery<any[]>({
    queryKey: ['/api/orders', Object.keys(filters).length > 0 ? `?${new URLSearchParams(filters as any).toString()}` : ''],
  });

  const apiRequest = useAuthApiRequest();


  const handleApplyFilters = () => {
    queryClient.invalidateQueries({ queryKey: ['/api/orders'] });
  };

  const handleClearFilters = () => {
    setFilters({
      subOrderNo: '',
      status: '',
      paymentStatus: '',
      dateFrom: '',
      dateTo: '',
    });
    queryClient.invalidateQueries({ queryKey: ['/api/orders'] });
  };

  const handleExportOrders = () => {
    const params = new URLSearchParams(filters);
    window.open(`/api/export/orders?${params}`, '_blank');
  };

  const getStatusBadge = (status: string) => {
    switch (status.toUpperCase()) {
      case 'DELIVERED':
        return <Badge className="bg-green-100 text-green-800">DELIVERED</Badge>;
      case 'RTO_COMPLETE':
        return <Badge variant="destructive">RTO_COMPLETE</Badge>;
      case 'CANCELLED':
        return <Badge className="bg-gray-100 text-gray-800">CANCELLED</Badge>;
      case 'RTO_LOCKED':
        return <Badge className="bg-yellow-100 text-yellow-800">RTO_LOCKED</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const getPaymentStatusBadge = (hasPayment: boolean) => {
    return hasPayment 
      ? <Badge className="bg-blue-100 text-blue-800">Paid</Badge>
      : <Badge variant="secondary">Pending</Badge>;
  };

  const formatCurrency = (amount: string | number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
    }).format(Number(amount));
  };

  const calculateProfit = (settlement: number, cost: number) => {
    const profit = settlement - cost;
    return {
      amount: profit,
      isProfit: profit >= 0,
      formatted: formatCurrency(profit),
    };
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Header title="Orders Management" subtitle="View and manage your orders data" />

      <div className="flex-1 p-6">
        {/* Filters Section */}
        <Card className="shadow-sm mb-6">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
              <Filter className="w-5 h-5" />
              Filters
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div>
                <Label htmlFor="sub-order-id">Sub Order ID</Label>
                <Input
                  id="sub-order-id"
                  placeholder="Search sub order ID..."
                  value={filters.subOrderNo}
                  onChange={(e) => setFilters({ ...filters, subOrderNo: e.target.value })}
                  data-testid="input-sub-order-id"
                />
              </div>
              <div>
                <Label htmlFor="order-status">Order Status</Label>
                <Select value={filters.status} onValueChange={(value) => setFilters({ ...filters, status: value })}>
                  <SelectTrigger data-testid="select-order-status">
                    <SelectValue placeholder="All Statuses" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Statuses</SelectItem>
                    <SelectItem value="DELIVERED">DELIVERED</SelectItem>
                    <SelectItem value="RTO_COMPLETE">RTO_COMPLETE</SelectItem>
                    <SelectItem value="CANCELLED">CANCELLED</SelectItem>
                    <SelectItem value="RTO_LOCKED">RTO_LOCKED</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="payment-status">Payment Status</Label>
                <Select value={filters.paymentStatus} onValueChange={(value) => setFilters({ ...filters, paymentStatus: value })}>
                  <SelectTrigger data-testid="select-payment-status">
                    <SelectValue placeholder="All Payments" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Payments</SelectItem>
                    <SelectItem value="paid">Paid</SelectItem>
                    <SelectItem value="pending">Pending</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="date-range">Date Range</Label>
                <Input
                  id="date-range"
                  type="date"
                  value={filters.dateFrom}
                  onChange={(e) => setFilters({ ...filters, dateFrom: e.target.value })}
                  data-testid="input-date-range"
                />
              </div>
            </div>
            <div className="flex justify-between items-center mt-4">
              <div className="flex gap-2">
                <Button onClick={handleApplyFilters} data-testid="button-apply-filters">
                  Apply Filters
                </Button>
                <Button variant="secondary" onClick={handleClearFilters} data-testid="button-clear-filters">
                  Clear
                </Button>
              </div>
              <div className="flex items-center gap-4">
                <label className="flex items-center gap-2">
                  <Checkbox 
                    checked={handleDuplicates}
                    onCheckedChange={setHandleDuplicates}
                    data-testid="checkbox-handle-duplicates"
                  />
                  <span className="text-sm">Handle Duplicates</span>
                </label>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Orders Table */}
        <Card className="shadow-sm">
          <CardContent className="p-0">
            <div className="p-6 border-b border-border">
              <div className="flex justify-between items-center">
                <h3 className="text-lg font-semibold">
                  Orders ({orders ? orders.length : 0} total)
                </h3>
                <div className="flex items-center gap-2">
                  <Button 
                    variant="secondary" 
                    size="sm" 
                    onClick={handleExportOrders}
                    data-testid="button-export-orders"
                  >
                    <Download className="w-4 h-4 mr-2" />
                    Export
                  </Button>
                </div>
              </div>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-muted/50">
                  <tr>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">S.No</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">SKU</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Sub Order ID</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Qty</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Order Date</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Listed Price</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Settlement</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Order Status</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Payment Status</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-border">
                  {isLoading ? (
                    <tr>
                      <td colSpan={9} className="px-4 py-8 text-center text-muted-foreground">
                        Loading orders...
                      </td>
                    </tr>
                  ) : orders && orders.length > 0 ? (
                    orders.map((order: any, index: number) => (
                      <tr key={order.id} className="hover:bg-muted/50" data-testid={`row-order-${order.subOrderNo}`}>
                        <td className="px-4 py-3 text-sm">{index + 1}</td>
                        <td className="px-4 py-3 text-sm font-mono">{order.sku}</td>
                        <td className="px-4 py-3 text-sm font-mono">{order.subOrderNo}</td>
                        <td className="px-4 py-3 text-sm">{order.quantity}</td>
                        <td className="px-4 py-3 text-sm">
                          {new Date(order.orderDate).toLocaleDateString()}
                        </td>
                        <td className="px-4 py-3 text-sm">{formatCurrency(order.listedPrice)}</td>
                        <td className="px-4 py-3 text-sm font-medium">
                          {formatCurrency(order.discountedPrice)}
                        </td>
                        <td className="px-4 py-3">
                          {getStatusBadge(order.reasonForCredit)}
                        </td>
                        <td className="px-4 py-3">
                          {getPaymentStatusBadge(false)} {/* Will be updated based on reconciliation */}
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan={9} className="px-4 py-8 text-center text-muted-foreground">
                        No orders found. Upload order files to get started.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/Products.tsx.content:
import { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import Header from '@/components/Header';
import { Package, Upload, Search } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useAuthQuery, useAuthApiRequest } from '@/hooks/use-auth-query';
import { useAuth } from '@/contexts/AuthContext';

export default function Products() {
  const [bulkPackagingCost, setBulkPackagingCost] = useState('');
  const [bulkGstPercent, setBulkGstPercent] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const apiRequest = useAuthApiRequest();

  const { data: products, isLoading } = useAuthQuery({
    queryKey: ['/api/products'],
  });

  const updateProductMutation = useMutation({
    mutationFn: async ({ sku, data }: { sku: string; data: any }) => {
      return apiRequest('PUT', `/api/products/${sku}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/products'] });
      toast({
        title: "Product updated",
        description: "Product has been updated successfully.",
      });
    },
  });

  const bulkUpdateMutation = useMutation({
    mutationFn: async ({ field, value }: { field: string; value: string }) => {
      return apiRequest('POST', '/api/products/bulk-update', { field, value });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/products'] });
      toast({
        title: "Bulk update completed",
        description: "All products have been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Bulk update failed",
        description: "There was an error updating the products.",
        variant: "destructive",
      });
    },
  });

  const handleProductUpdate = (sku: string, field: string, value: string) => {
    updateProductMutation.mutate({
      sku,
      data: { [field]: value }
    });
  };

  const handleBulkSetPackaging = () => {
    if (!bulkPackagingCost) {
      toast({
        title: "Missing value",
        description: "Please enter a packaging cost value.",
        variant: "destructive",
      });
      return;
    }
    bulkUpdateMutation.mutate({
      field: 'packagingCost',
      value: bulkPackagingCost
    });
  };

  const handleBulkSetGST = () => {
    if (!bulkGstPercent) {
      toast({
        title: "Missing value", 
        description: "Please enter a GST percentage value.",
        variant: "destructive",
      });
      return;
    }
    bulkUpdateMutation.mutate({
      field: 'gstPercent',
      value: bulkGstPercent
    });
  };

  const filteredProducts = products?.filter((product: any) =>
    product.sku.toLowerCase().includes(searchQuery.toLowerCase()) ||
    product.title.toLowerCase().includes(searchQuery.toLowerCase())
  ) || [];

  return (
    <div className="min-h-screen flex flex-col">
      <Header title="Products" subtitle="Manage product costs and configurations" />

      <div className="flex-1 p-6">
        {/* Product Management Header */}
        <Card className="shadow-sm mb-6">
          <CardContent className="p-6">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold flex items-center gap-2">
                <Package className="w-5 h-5" />
                Product Cost Management
              </h3>
              <div className="flex items-center gap-4">
                <Button variant="secondary" data-testid="button-import-products">
                  <Upload className="w-4 h-4 mr-2" />
                  Import Products
                </Button>
                <Button data-testid="button-update-all-products">
                  Update All Product Costs
                </Button>
              </div>
            </div>

            {/* Bulk Actions */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <Label htmlFor="bulk-packaging">Bulk Set Packaging Cost</Label>
                <div className="flex gap-2">
                  <Input
                    id="bulk-packaging"
                    type="number"
                    step="0.01"
                    placeholder="â‚¹ Amount"
                    value={bulkPackagingCost}
                    onChange={(e) => setBulkPackagingCost(e.target.value)}
                    data-testid="input-bulk-packaging-cost"
                  />
                  <Button 
                    variant="outline" 
                    onClick={handleBulkSetPackaging}
                    disabled={bulkUpdateMutation.isPending}
                    data-testid="button-bulk-set-packaging"
                  >
                    Apply to All
                  </Button>
                </div>
              </div>
              <div>
                <Label htmlFor="bulk-gst">Bulk Set GST %</Label>
                <div className="flex gap-2">
                  <Input
                    id="bulk-gst"
                    type="number"
                    step="0.1"
                    placeholder="% Rate"
                    value={bulkGstPercent}
                    onChange={(e) => setBulkGstPercent(e.target.value)}
                    data-testid="input-bulk-gst-percent"
                  />
                  <Button 
                    variant="outline" 
                    onClick={handleBulkSetGST}
                    disabled={bulkUpdateMutation.isPending}
                    data-testid="button-bulk-set-gst"
                  >
                    Apply to All
                  </Button>
                </div>
              </div>
              <div>
                <Label htmlFor="search">Search Products</Label>
                <div className="relative">
                  <Search className="w-4 h-4 absolute left-3 top-3 text-muted-foreground" />
                  <Input
                    id="search"
                    placeholder="Search by SKU or title..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pl-9"
                    data-testid="input-search-products"
                  />
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Products Table */}
        <Card className="shadow-sm">
          <CardContent className="p-0">
            <div className="p-6 border-b border-border">
              <h3 className="text-lg font-semibold">
                Products ({filteredProducts.length} total)
              </h3>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-muted/50">
                  <tr>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">SKU</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Product Title</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Cost Price (â‚¹)</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Packaging Cost (â‚¹)</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">GST (%)</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Total Orders</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Last Updated</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-border">
                  {isLoading ? (
                    <tr>
                      <td colSpan={8} className="px-4 py-8 text-center text-muted-foreground">
                        Loading products...
                      </td>
                    </tr>
                  ) : filteredProducts.length > 0 ? (
                    filteredProducts.map((product: any) => (
                      <tr key={product.id} className="hover:bg-muted/50" data-testid={`row-product-${product.sku}`}>
                        <td className="px-4 py-3 text-sm font-mono">{product.sku}</td>
                        <td className="px-4 py-3 text-sm" title={product.title}>
                          {product.title.length > 50 ? `${product.title.slice(0, 50)}...` : product.title}
                        </td>
                        <td className="px-4 py-3 text-sm">
                          <Input
                            type="number"
                            step="0.01"
                            defaultValue={product.costPrice || '0'}
                            onBlur={(e) => handleProductUpdate(product.sku, 'costPrice', e.target.value)}
                            className="w-20"
                            data-testid={`input-cost-price-${product.sku}`}
                          />
                        </td>
                        <td className="px-4 py-3 text-sm">
                          <Input
                            type="number"
                            step="0.01"
                            defaultValue={product.packagingCost || '0'}
                            onBlur={(e) => handleProductUpdate(product.sku, 'packagingCost', e.target.value)}
                            className="w-20"
                            data-testid={`input-packaging-cost-${product.sku}`}
                          />
                        </td>
                        <td className="px-4 py-3 text-sm">
                          <Input
                            type="number"
                            step="0.1"
                            defaultValue={product.gstPercent || '18'}
                            onBlur={(e) => handleProductUpdate(product.sku, 'gstPercent', e.target.value)}
                            className="w-16"
                            data-testid={`input-gst-percent-${product.sku}`}
                          />
                        </td>
                        <td className="px-4 py-3 text-sm text-muted-foreground">
                          {product.totalOrders || 0}
                        </td>
                        <td className="px-4 py-3 text-sm text-muted-foreground">
                          {new Date(product.updatedAt).toLocaleString()}
                        </td>
                        <td className="px-4 py-3 text-sm">
                          <Button 
                            variant="link" 
                            size="sm"
                            data-testid={`button-save-${product.sku}`}
                          >
                            Save
                          </Button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan={8} className="px-4 py-8 text-center text-muted-foreground">
                        No products found. {searchQuery ? 'Try adjusting your search.' : 'Upload order files to create products.'}
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/Reconciliation.tsx.content:
import { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import Header from '@/components/Header';
import { Calculator, CheckCircle, X, AlertTriangle, Download } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useAuthQuery, useAuthApiRequest } from '@/hooks/use-auth-query';
import { useAuth } from '@/contexts/AuthContext';

interface ReconciliationFilters {
  dateFrom: string;
  dateTo: string;
  tolerance: string;
  status: string;
}

export default function Reconciliation() {
  const [filters, setFilters] = useState<ReconciliationFilters>({
    dateFrom: '',
    dateTo: '',
    tolerance: '1.00',
    status: '',
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const apiRequest = useAuthApiRequest();

  const { data: reconciliations, isLoading: reconciliationsLoading } = useAuthQuery({
    queryKey: ['/api/reconciliations', filters.status],
  });

  const { data: summary, isLoading: summaryLoading } = useAuthQuery({
    queryKey: ['/api/reconciliations/summary'],
  });

  const reconciliationMutation = useMutation({
    mutationFn: async () => {
      return apiRequest('POST', '/api/reconciliations/run', {});
    },
    onSuccess: (response) => {
      const data = response.json();
      queryClient.invalidateQueries({ queryKey: ['/api/reconciliations'] });
      queryClient.invalidateQueries({ queryKey: ['/api/reconciliations/summary'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/summary'] });
      toast({
        title: "Reconciliation completed",
        description: `Processed ${data.processed} records. ${data.reconciled} reconciled, ${data.mismatched} mismatched, ${data.unreconciled} unreconciled.`,
      });
    },
    onError: () => {
      toast({
        title: "Reconciliation failed",
        description: "There was an error running the reconciliation engine.",
        variant: "destructive",
      });
    },
  });

  const handleRunReconciliation = () => {
    reconciliationMutation.mutate();
  };

  const handleExportResults = () => {
    const params = new URLSearchParams({ status: filters.status });
    window.open(`/api/export/reconciliations?${params}`, '_blank');
  };

  const getStatusBadge = (status: string) => {
    switch (status.toLowerCase()) {
      case 'reconciled':
        return (
          <Badge className="bg-green-100 text-green-800">
            <CheckCircle className="w-3 h-3 mr-1" />
            Reconciled
          </Badge>
        );
      case 'mismatch':
        return (
          <Badge className="bg-yellow-100 text-yellow-800">
            <AlertTriangle className="w-3 h-3 mr-1" />
            Mismatch
          </Badge>
        );
      case 'unreconciled':
        return (
          <Badge variant="secondary">
            <X className="w-3 h-3 mr-1" />
            Unreconciled
          </Badge>
        );
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const formatCurrency = (amount: string | number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
    }).format(Number(amount));
  };

  const summaryData = summary || {
    reconciled: 0,
    mismatch: 0,
    unreconciled: 0,
    successRate: 0,
  };

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      <Header title="Reconciliation" subtitle="Review and reconcile payment discrepancies" />

      <div className="flex-1 overflow-auto p-6">
        {/* Reconciliation Controls */}
        <Card className="shadow-sm mb-6">
          <CardContent className="p-6">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold flex items-center gap-2">
                <Calculator className="w-5 h-5" />
                Reconciliation Engine
              </h3>
              <Button 
                onClick={handleRunReconciliation}
                disabled={reconciliationMutation.isPending}
                data-testid="button-run-full-reconciliation"
              >
                {reconciliationMutation.isPending ? 'Running...' : 'Run Full Reconciliation'}
              </Button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <Label htmlFor="date-from">Date From</Label>
                <Input
                  id="date-from"
                  type="date"
                  value={filters.dateFrom}
                  onChange={(e) => setFilters({ ...filters, dateFrom: e.target.value })}
                  data-testid="input-date-from"
                />
              </div>
              <div>
                <Label htmlFor="date-to">Date To</Label>
                <Input
                  id="date-to"
                  type="date"
                  value={filters.dateTo}
                  onChange={(e) => setFilters({ ...filters, dateTo: e.target.value })}
                  data-testid="input-date-to"
                />
              </div>
              <div>
                <Label htmlFor="tolerance">Tolerance (â‚¹)</Label>
                <Input
                  id="tolerance"
                  type="number"
                  step="0.01"
                  value={filters.tolerance}
                  onChange={(e) => setFilters({ ...filters, tolerance: e.target.value })}
                  data-testid="input-tolerance"
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Reconciliation Summary */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <Card className="shadow-sm" data-testid="card-reconciled-orders">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Reconciled Orders</p>
                  <p className="text-2xl font-bold text-green-600" data-testid="text-reconciled-count">
                    {summaryLoading ? '...' : summaryData.reconciled.toLocaleString()}
                  </p>
                </div>
                <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
                  <CheckCircle className="w-5 h-5 text-green-600" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="shadow-sm" data-testid="card-mismatched">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Mismatched</p>
                  <p className="text-2xl font-bold text-yellow-600" data-testid="text-mismatch-count">
                    {summaryLoading ? '...' : summaryData.mismatch.toLocaleString()}
                  </p>
                </div>
                <div className="w-10 h-10 bg-yellow-100 rounded-full flex items-center justify-center">
                  <AlertTriangle className="w-5 h-5 text-yellow-600" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="shadow-sm" data-testid="card-unreconciled">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Unreconciled</p>
                  <p className="text-2xl font-bold text-red-600" data-testid="text-unreconciled-count">
                    {summaryLoading ? '...' : summaryData.unreconciled.toLocaleString()}
                  </p>
                </div>
                <div className="w-10 h-10 bg-red-100 rounded-full flex items-center justify-center">
                  <X className="w-5 h-5 text-red-600" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="shadow-sm" data-testid="card-success-rate">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-muted-foreground text-sm">Success Rate</p>
                  <p className="text-2xl font-bold text-blue-600" data-testid="text-success-rate">
                    {summaryLoading ? '...' : `${summaryData.successRate.toFixed(1)}%`}
                  </p>
                </div>
                <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                  <Calculator className="w-5 h-5 text-blue-600" />
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Reconciliation Details */}
        <Card className="shadow-sm">
          <CardContent className="p-0">
            <div className="p-6 border-b border-border">
              <div className="flex justify-between items-center">
                <h3 className="text-lg font-semibold">Reconciliation Details</h3>
                <div className="flex items-center gap-4">
                  <Select value={filters.status} onValueChange={(value) => setFilters({ ...filters, status: value })}>
                    <SelectTrigger className="w-40" data-testid="select-status-filter">
                      <SelectValue placeholder="All Status" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Status</SelectItem>
                      <SelectItem value="reconciled">Reconciled</SelectItem>
                      <SelectItem value="mismatch">Mismatch</SelectItem>
                      <SelectItem value="unreconciled">Unreconciled</SelectItem>
                    </SelectContent>
                  </Select>
                  <Button 
                    variant="secondary" 
                    onClick={handleExportResults}
                    data-testid="button-export-results"
                  >
                    <Download className="w-4 h-4 mr-2" />
                    Export Results
                  </Button>
                </div>
              </div>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-muted/50">
                  <tr>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Sub Order ID</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">SKU</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Order Value</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Settlement</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Product Cost</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Ads Cost</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Net Profit</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Status</th>
                    <th className="px-4 py-3 text-left text-sm font-medium text-muted-foreground">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-border">
                  {reconciliationsLoading ? (
                    <tr>
                      <td colSpan={9} className="px-4 py-8 text-center text-muted-foreground">
                        Loading reconciliation data...
                      </td>
                    </tr>
                  ) : reconciliations && reconciliations.length > 0 ? (
                    reconciliations.map((reconciliation: any) => {
                      const netProfit = Number(reconciliation.netProfit) || 0;
                      const isProfit = netProfit >= 0;

                      return (
                        <tr key={reconciliation.id} className="hover:bg-muted/50" data-testid={`row-reconciliation-${reconciliation.subOrderNo}`}>
                          <td className="px-4 py-3 text-sm font-mono">{reconciliation.subOrderNo}</td>
                          <td className="px-4 py-3 text-sm font-mono">
                            {/* This would need to be joined from the order data */}
                            -
                          </td>
                          <td className="px-4 py-3 text-sm">{formatCurrency(reconciliation.orderValue)}</td>
                          <td className="px-4 py-3 text-sm font-medium">{formatCurrency(reconciliation.settlementAmount)}</td>
                          <td className="px-4 py-3 text-sm">{formatCurrency(reconciliation.productCost)}</td>
                          <td className="px-4 py-3 text-sm">{formatCurrency(reconciliation.adsCost)}</td>
                          <td className={`px-4 py-3 text-sm font-medium ${isProfit ? 'text-green-600' : 'text-red-600'}`}>
                            {isProfit ? '+' : ''}{formatCurrency(netProfit)}
                          </td>
                          <td className="px-4 py-3">
                            {getStatusBadge(reconciliation.status)}
                          </td>
                          <td className="px-4 py-3 text-sm">
                            <Button 
                              variant="link" 
                              size="sm"
                              data-testid={`button-view-details-${reconciliation.subOrderNo}`}
                            >
                              View
                            </Button>
                          </td>
                        </tr>
                      );
                    })
                  ) : (
                    <tr>
                      <td colSpan={9} className="px-4 py-8 text-center text-muted-foreground">
                        No reconciliation data found. Run reconciliation to generate results.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/Upload.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import Header from '@/components/Header';
import { Upload as UploadIcon, FileText, Archive } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest, getQueryFn } from '@/lib/queryClient';
import { useAuth } from '@/contexts/AuthContext';

export default function Upload() {
  const [paymentFiles, setPaymentFiles] = useState<FileList | null>(null);
  const [ordersFiles, setOrdersFiles] = useState<FileList | null>(null);
  const [sourceMonth, setSourceMonth] = useState('');
  const [label, setLabel] = useState('');
  const { toast } = useToast();
  const { token } = useAuth();
  const queryClient = useQueryClient();

  const { data: uploads = [], isLoading } = useQuery({
    queryKey: ['/api/uploads'],
    queryFn: getQueryFn({ on401: "returnNull", token }),
    enabled: !!token,
  });

  const uploadMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      const response = await fetch('/api/upload', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });
      if (!response.ok) {
        throw new Error('Upload failed');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/uploads'] });
      toast({
        title: "Upload started",
        description: "Your files are being processed.",
      });

      // Start polling for completion and refresh UI when both files are processed
      const pollForCompletion = () => {
        setTimeout(async () => {
          try {
            const response = await fetch('/api/uploads', {
              headers: { 'Authorization': `Bearer ${token}` },
            });
            const uploads = await response.json();

            // Check if all recent uploads are processed
            const recentUploads = uploads.filter((upload: any) => 
              new Date(upload.createdAt) > new Date(Date.now() - 5 * 60 * 1000) // Last 5 minutes
            );

            const allProcessed = recentUploads.length > 0 && 
              recentUploads.every((upload: any) => upload.status === 'processed' || upload.status === 'failed');

            if (allProcessed) {
              // Refresh all data when processing is complete
              queryClient.invalidateQueries({ queryKey: ['/api/dashboard/summary'] });
              queryClient.invalidateQueries({ queryKey: ['/api/orders'] });
              queryClient.invalidateQueries({ queryKey: ['/api/products'] });
              queryClient.invalidateQueries({ queryKey: ['/api/uploads'] });

              toast({
                title: "Processing completed",
                description: "Your files have been processed and data updated.",
              });
            } else if (recentUploads.some((upload: any) => upload.status === 'processing')) {
              pollForCompletion(); // Continue polling
            }
          } catch (error) {
            // Silently handle polling errors
          }
        }, 2000); // Poll every 2 seconds
      };

      pollForCompletion();

      // Reset form
      setPaymentFiles(null);
      setOrdersFiles(null);
      setSourceMonth('');
      setLabel('');
    },
    onError: () => {
      toast({
        title: "Upload failed",
        description: "There was an error uploading your files.",
        variant: "destructive",
      });
    },
  });

  const handleProcessFiles = async () => {
    if (!paymentFiles && !ordersFiles) {
      toast({
        title: "No files selected",
        description: "Please select at least one file to process.",
        variant: "destructive",
      });
      return;
    }

    const uploads = [];

    if (paymentFiles) {
      const formData = new FormData();
      formData.append('file', paymentFiles[0]);
      formData.append('fileType', 'payment_zip');
      formData.append('sourceMonth', sourceMonth);
      uploads.push(formData);
    }

    if (ordersFiles) {
      const formData = new FormData();
      formData.append('file', ordersFiles[0]);
      formData.append('fileType', 'orders_csv');
      formData.append('label', label);
      uploads.push(formData);
    }

    // Process uploads sequentially
    for (const formData of uploads) {
      await uploadMutation.mutateAsync(formData);
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'processed':
        return <Badge className="bg-green-100 text-green-800">Processed</Badge>;
      case 'processing':
        return <Badge className="bg-yellow-100 text-yellow-800">Processing</Badge>;
      case 'failed':
        return <Badge variant="destructive">Failed</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Header 
        title="Upload Files" 
        subtitle="Upload and process payment and order files"
      />

      <div className="flex-1 p-6">
        {/* Upload Sections */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Payment Files Upload */}
          <Card className="shadow-sm" data-testid="card-payment-upload">
            <CardContent className="p-6">
              <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                <Archive className="w-5 h-5 text-blue-600" />
                Payment ZIP Files
              </h3>

              <div 
                className="border-2 border-dashed border-border rounded-lg p-8 text-center hover:border-primary transition-colors cursor-pointer"
                onClick={() => document.getElementById('payment-file')?.click()}
                data-testid="dropzone-payment"
              >
                <UploadIcon className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-lg font-medium">Drop payment ZIP files here</p>
                <p className="text-muted-foreground mt-2">or click to browse</p>
                <p className="text-sm text-muted-foreground mt-2">Supports ZIP files containing XLSX payment sheets</p>
              </div>

              <input
                id="payment-file"
                type="file"
                accept=".zip"
                className="hidden"
                onChange={(e) => setPaymentFiles(e.target.files)}
                data-testid="input-payment-file"
              />

              {paymentFiles && (
                <div className="mt-4 p-3 bg-muted rounded-md">
                  <p className="text-sm font-medium">Selected: {paymentFiles[0].name}</p>
                </div>
              )}

              <div className="mt-4">
                <Label htmlFor="source-month">Source Month (Optional)</Label>
                <Input
                  id="source-month"
                  type="month"
                  value={sourceMonth}
                  onChange={(e) => setSourceMonth(e.target.value)}
                  data-testid="input-source-month"
                />
              </div>
            </CardContent>
          </Card>

          {/* Orders CSV Upload */}
          <Card className="shadow-sm" data-testid="card-orders-upload">
            <CardContent className="p-6">
              <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                <FileText className="w-5 h-5 text-green-600" />
                Orders CSV Files
              </h3>

              <div 
                className="border-2 border-dashed border-border rounded-lg p-8 text-center hover:border-primary transition-colors cursor-pointer"
                onClick={() => document.getElementById('orders-file')?.click()}
                data-testid="dropzone-orders"
              >
                <FileText className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-lg font-medium">Drop orders CSV files here</p>
                <p className="text-muted-foreground mt-2">or click to browse</p>
                <p className="text-sm text-muted-foreground mt-2">Supports CSV files with order data</p>
              </div>

              <input
                id="orders-file"
                type="file"
                accept=".csv"
                className="hidden"
                onChange={(e) => setOrdersFiles(e.target.files)}
                data-testid="input-orders-file"
              />

              {ordersFiles && (
                <div className="mt-4 p-3 bg-muted rounded-md">
                  <p className="text-sm font-medium">Selected: {ordersFiles[0].name}</p>
                </div>
              )}

              <div className="mt-4">
                <Label htmlFor="label">Label (Optional)</Label>
                <Input
                  id="label"
                  type="text"
                  placeholder="e.g., August 2024 Orders"
                  value={label}
                  onChange={(e) => setLabel(e.target.value)}
                  data-testid="input-label"
                />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Process Files Button */}
        <div className="text-center mb-8">
          <Button 
            size="lg"
            onClick={handleProcessFiles}
            disabled={uploadMutation.isPending}
            data-testid="button-process-files"
          >
            {uploadMutation.isPending ? 'Processing...' : 'Process Files'}
          </Button>
        </div>

        {/* Upload History */}
        <Card className="shadow-sm">
          <CardContent className="p-0">
            <div className="p-6 border-b border-border">
              <h3 className="text-lg font-semibold">Upload History</h3>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-muted/50">
                  <tr>
                    <th className="px-6 py-3 text-left text-sm font-medium text-muted-foreground">File Name</th>
                    <th className="px-6 py-3 text-left text-sm font-medium text-muted-foreground">Type</th>
                    <th className="px-6 py-3 text-left text-sm font-medium text-muted-foreground">Status</th>
                    <th className="px-6 py-3 text-left text-sm font-medium text-muted-foreground">Records</th>
                    <th className="px-6 py-3 text-left text-sm font-medium text-muted-foreground">Uploaded</th>
                    <th className="px-6 py-3 text-left text-sm font-medium text-muted-foreground">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-border">
                  {isLoading ? (
                    <tr>
                      <td colSpan={6} className="px-6 py-8 text-center text-muted-foreground">
                        Loading upload history...
                      </td>
                    </tr>
                  ) : Array.isArray(uploads) && uploads.length > 0 ? (
                    uploads.map((upload: any) => (
                      <tr key={upload.id} className="hover:bg-muted/50" data-testid={`row-upload-${upload.id}`}>
                        <td className="px-6 py-4 text-sm font-medium">{upload.originalName}</td>
                        <td className="px-6 py-4 text-sm">
                          {upload.fileType === 'payment_zip' ? 'Payment ZIP' : 'Orders CSV'}
                        </td>
                        <td className="px-6 py-4">
                          {getStatusBadge(upload.status)}
                        </td>
                        <td className="px-6 py-4 text-sm text-muted-foreground">
                          {upload.recordsProcessed > 0 
                            ? `${upload.recordsProcessed} records` 
                            : '-'
                          }
                        </td>
                        <td className="px-6 py-4 text-sm text-muted-foreground">
                          {new Date(upload.createdAt).toLocaleString()}
                        </td>
                        <td className="px-6 py-4 text-sm">
                          <Button variant="link" size="sm" data-testid={`button-view-${upload.id}`}>
                            View
                          </Button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan={6} className="px-6 py-8 text-center text-muted-foreground">
                        No uploads yet. Upload your first file to get started.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

